import React, { useContext } from 'react';
import { _Context } from '../../App';
import { Slider } from 'antd';

const Bumpy = () => {
  const { state: { water }, dispatch } = useContext(_Context);

  const setWater = payload => 
    dispatch({type: 'UPDATE_WATER', payload});

  return (
    <Slider 
      range 
      defaultValue={50} 
      value={water}
      max={100}
      min={0}
      onChange={ setBumpy }
    />
  )
}

export default setWater;
