import React, { useContext } from 'react';
import { _Context } from '../../App';
import { Slider } from 'antd';

const Bumpy = () => {
  const { state, dispatch } = useContext(_Context);
  const { bumpy, water, temperature } = state;

  const setBumpy = payload => 
    dispatch({type: 'UPDATE_BUMPY', payload});

  const setWater = payload => 
    dispatch({type: 'UPDATE_WATER', payload});

  const setTemp = payload =>
    dispatch({type: 'UPDATE_TEMPERATURE', payload});

  const createSlider = (min, max, set) => ({val}) => (
    <Slider 
      style={{ width: 200 }}
      range 
      defaultValue={[0, 100]} 
      value={val}
      max={max}
      min={min}
      onChange={ set }
    />
  );

  const WaterSlider = createSlider(0, 100, setWater);
  const BumpySlider = createSlider(0, 100, setBumpy);
  const TempSlider = createSlider(0, 100, setTemp);

  return (
    <>
      <WaterSlider val={water} />
      <BumpySlider val={bumpy} />
      <TempSlider val={temperature} />
    </>
  )
}

export default Bumpy;
