import React, { useContext } from 'react';
import { _Context } from '../../App';
import { Slider as AntdSlider } from 'antd';

const Slider = ({ type, item, max, min }) => {
  const { state, dispatch } = useContext(_Context);
  const value = state[item];

  const set = payload => 
    dispatch({type: 'UPDATE_'+type, payload});

  return (
    <Slider 
      style={{ width: 200 }}
      range 
      defaultValue={[0, 100]} 
      value={value}
      max={max}
      min={min}
      onChange={ set }
    />
  )
}

const Range = () => {

  return (
    <>
      <Slider 
        type="WATER"
        item="water"
        max={max}
        min={min}
      />
      <Slider 
        type="BUMPY"
        item="bumpy"
        max={max}
        min={min}
      />
      <Slider 
        type="TEMPERATURE"
        item="temperature"
        max={max}
        min={min}
      />
    </>
  )
}

export default Range;
