import { ALL_COUNTRIES } from './data';

export const initialState = {
  country: null,
  land: null
};

export function reducer(state, action) {
  switch (action.type) {
    case 'UPDATE_COUNTRY':
      return { 
        country: action.payload
      };
    case 'UPDATE_LAND':
      return { 
        land: action.payload
      };
    default:
      throw new Error();
  }
}
